    <script src=""https://code.jquery.com/jquery-3.6.0.min.js""></script>
    <script>
        $(document).ready(function () {
            function loadBangChamCong() {
                var loai = $('input[name=""loaiCong""]:checked').val();
                var nam = $('#selectYear').val();
                var quy = $('#selectQuarter').val();
                
                console.log('Loading bảng chấm công:', { loai: loai, nam: nam, quy: quy });

                $.ajax({
                    url: '/TimeKeeping/RenderLoaiCong',
                    type: 'GET',
                    data: { loai: loai, nam: nam, quy: quy },
                    success: function (data) {
                        console.log('AJAX success, data length:', data.length);
                        $('#bangKetQua').html(data);
                        
                        // Trigger resize event để Handsontable tự động điều chỉnh kích thước
                        $(window).trigger('resize');
                    },
                    er");
                WriteLiteral(@"ror: function (xhr, status, error) {
                        console.error('AJAX error:', { status: status, error: error, response: xhr.responseText });
                        alert(""Lỗi khi tải dữ liệu: "" + error);
                    }
                });
            }

            // Khi ấn nút ""Tìm kiếm""
            $('#btnTimKiem').click(function () {
                console.log('Button Tìm kiếm clicked');
                loadBangChamCong();
            });

            // ✅ Khi chọn radio ""loaiCong"" → tự động gọi
            $('input[name=""loaiCong""]').change(function () {
                console.log('Radio button changed to:', $(this).val());
                loadBangChamCong();
            });

