                var nam = $('#selectYear').val();
                var quy = $('#selectQuarter').val();
                


                $.ajax({
                    url: '/TimeKeeping/RenderLoaiCong',
                    type: 'GET',
                    data: { loai: loai, nam: nam, quy: quy, _: new Date().getTime() }, // Thêm timestamp để tránh cache
                    success: function (data) {
                        $('#bangKetQua').html(data);
                        $(window).trigger('resize');
