    <script src=""https://code.jquery.com/jquery-3.6.0.min.js""></script>
    <script>
        $(document).ready(function () {
            // Tự động set selected cho quý và năm hiện tại
            var currentMonth = new Date().getMonth() + 1; // 0-11 -> 1-12
            var currentYear = new Date().getFullYear();
            
            // Set quý hiện tại
            if (currentMonth <= 3) {
                $('#selectQuarter').val('1');
            } else if (currentMonth <= 6) {
                $('#selectQuarter').val('2');
            } else if (currentMonth <= 9) {
                $('#selectQuarter').val('3');
            } else {
                $('#selectQuarter').val('4');
            }
            
            // Set năm hiện tại
            $('#selectYear').val(currentYear);
            
            function loadBangChamCong() {
                var loai = $('input[name=""loaiCong""]:checked').val();
                var quy = $('#selectQuarter').val();
                var nam = $('#selectYear').val();");
