                    url: '/TimeKeeping/RenderLoaiCong',
                    type: 'GET',
                    data: { loai: loai, quy: quy, nam: nam },
                    success: function (data) {
                        $('#bangKetQua').html(data);
                    },
                    error: function () {
                        alert(""Lỗi khi tải dữ liệu."");
                    }
                });
            }

            // Khi ấn nút ""Tìm kiếm""
